	<style type="text/css">
		body{background:url(../img/background1.jpg);background-attachment:fixed;background-position:center;background-repeat:no-repeat;background-size:cover;background-color:#fff;color:#f3d030;font-family:Michroma;min-width:320px}a{color:#f3d030}p{text-align:justify}section{background-color:rgba(0,0,0,0.5)}section h3,section h4{color:#fff}nav{background-color:#0B173B}select option{background-color:#fff;width:100%}.btn-large i{font-size:30px}.row{padding-bottom:20px;margin-bottom:0}.bgWhite{color:#000;background-color:rgba(236,239,241,.8)}.bgWhite h3,.bgWhite h4{color:#0B173B}.noheight{height:0;opacity:0}#preloader{position:fixed;width:100%;height:100%;top:0;left:0;z-index:1000;background-color:#000;background:url(../img/background1.jpg);overflow:hidden}#preloader .row{text-align:center}#preloader img{width:200px;margin-bottom:20px}#preloader h6{margin-bottom:10px}#preloader .progress{background-color:#fff;width:200px;margin:0 auto}#preloader .progress .indeterminate{background-color:#f3d030}#logoHeader,#redesSociales{padding:10px 20px}#header .brand-logo img{width:100px}#header h5{font-size:2.5vw}.brand-logo.row{margin:0}#redesSociales div a{margin:3px 5px}#redesSociales .chip{padding-left:0;height:37px;color:#000;font-size:10px}#redesSociales .chip i{color:#fff;padding:8px;border-radius:50%;margin-right:8px;font-size:20px;height:37px;width:37px;text-align:center;margin-left:-5px}#listaRedes1{background-color:transparent;margin-top:10px;border-top:1px solid #fff;padding:0;min-width:65px;box-shadow:none}#listaRedes1 li a{padding:0;margin:10px auto}.nav-wrapper .brand-logo img{height:60px;margin:2px auto}.navbar-fixed{position:absolute;top:0}#mobileMenuIcon{background-color:#0B173B;position:fixed;top:0;left:0;margin:10px;z-index:998}.side-nav{background-color:rgba(4,9,23,.8)}.side-nav a{color:#fff}#slider{position:relative;height:70vh}.slider .slides li .caption{text-shadow:3px 3px 10px #000,-3px -3px 10px #000}.slider .slides li .caption h3{color:#fff}.modal-content ul,.modal-content ul li{list-style-type:initial;text-align:justify}.modal-content ul{margin-left:18px}#cards .row .col{padding:0}.modal{color:#000;background-color:rgba(236,239,241,.95);width:95%}.modal h5,.modal h6{font-weight:700;display:inline-block;color:#0B173B}.modal.modal-fixed-header{top:5%!important;height:90%;max-height:90%}.modal.modal-fixed-header .modal-header{padding:10px 24px;color:#0B173B;font-weight:700;border-bottom:1px solid #01579b;position:absolute;width:100%}.modal.modal-fixed-header .modal-content{position:absolute;padding:10px 24px;height:calc(100% - 70px);max-height:100%;width:100%;overflow-y:auto;bottom:0}.modal img{width:100%}@media only screen and (max-width: 992px){#header h5{font-size:3.5vw}.modal{width:90%}}
